def currency_name_code(currency):
#    currency_name, currency_code = main()
    if currency.upper() in currency_code:
        return currency_name[currency_code.index(currency.upper())], currency
    else:
        return '-1', '-1'



def parse_text(text):
    pattern = r'/\w+'
    crypto = re.search(pattern, text).group()
    return crypto[1:]


def price_coinmarket(crypto):
    url = 'https://api.coinmarketcap.com/v1/ticker/{}'.format(crypto)
    r = requests.get(url).json()
    price = r[-1]['price_usd']
    # write_json(r.json(), filename='price.json')
    return '1 {} = '.format(crypto) + str(price) + ' usd' + ' на бирже coinmarketcap.com'


def price_yobit(crypto):
    url = 'https://yobit.net/api/2/{}_usd/ticker'.format(crypto)
    response = requests.get(url).json()
    price = response['ticker']['last']
    return '1 {} = '.format(crypto) + str(price) + ' usd' + ' на бирже yobit.net'



def get_price(crypto):
    name, code = currency_name_code(crypto)
    name = name.lower()
    code = code.lower()
    if name == '-1':
        return 'Молодой человек, Вы плохо себя ведете...'
    else:
        return str(price_coinmarket(name)) + '\n' + str(price_yobit(code))







